option.value:
import {NextResponse} from 'next/server'
import { updateOrder } from '@/ui/hooks/store/useUpdateOrderActions'

export async function POST(req: Request) {
    try {
        const body = await req.json()
        // basic validation
        if (!body || typeof body.order_id === 'undefined' || typeof body.order_status === 'undefined') {
            return NextResponse.json({error: 'Invalid payload'}, {status: 400})
        }

        const result = await updateOrder(body)

        if (result?.error) {
            return NextResponse.json({error: result.error}, {status: 500})
        }

        return NextResponse.json({ok: true})
    } catch (err: any) {
        console.error('[api/admin/orders/update] Error:', err)
        return NextResponse.json({error: err?.message ?? 'Internal error'}, {status: 500})
    }
}
